import React, { Component } from 'react';
import MovieID from './Movie';
import Axios from 'axios';
import { PopupboxManager, PopupboxContainer } from 'react-popupbox';
import 'react-popupbox/dist/react-popupbox.css';

export class Movies extends Component {
	constructor() {
		super();
		this.state = {
			movies: []
		};
		this.wrapper = React.createRef();
	}

	componentDidMount() {
		MovieID.split('\n').map((id, i) => {
			return Axios.get('https://www.omdbapi.com/?apikey=' + 'ec05d9ae' + '&i=' + id)
			.then((response) => {
				let movies = this.state.movies;
				movies[i] = response.data

				this.setState({movies});
			});
		});
	}

	// getMoviePoster = (index) => {
	// 	if (index + 1 === 1) {
	// 		return Movie1;
	// 	} else if (index + 1 === 2) {
	// 		return Movie2;
	// 	} else if (index + 1 === 3) {
	// 		return Movie3;
	// 	} else if (index + 1 === 4) {
	// 		return Movie4;
	// 	} else if (index + 1 === 5) {
	// 		return Movie5;
	// 	} else if (index + 1 === 6) {
	// 		return Movie6;
	// 	} else if (index + 1 === 7) {
	// 		return Movie7;
	// 	} else if (index + 1 === 8) {
	// 		return Movie8;
	// 	} else if (index + 1 === 9) {
	// 		return Movie9;
	// 	} else if (index + 1 === 10) {
	// 		return Movie10;
	// 	} else if (index + 1 === 11) {
	// 		return Movie11;
	// 	} else if (index + 1 === 12) {
	// 		return Movie12;
	// 	}
	// }

	lockScroll = () => {
		console.log('lock')
		document.body.style.overflow = 'hidden'
	}

	unlockScroll = () => {
		console.log('unlock')
		document.body.style.overflow = 'inherit'
	}

	displayMovies = () => {
		return this.state.movies.map((movie) => (
			<img className="movie" src={movie.Poster}
                key={movie.Title} alt=""
                onMouseEnter={this.dimPoster} onMouseLeave={this.resetPoster}
				onClick={this.displayLightbox.bind(this, movie)}/>
		));
	}

	displayLightbox = (movie) => {
		const content = (

			<div className="movLightbox-container">
				<img className="movLightbox-content" src={movie.Poster} alt=""/>
				<div className = "movLightbox-info">
					<span className = "title">{movie.Title}</span>
					<br></br>
					<br></br>
					<span className = "director">Directed by: <b>{movie.Director}</b></span>
					<br></br>
					<br></br>
					<br></br>
					<br></br>
					<p>{movie.Plot}</p>
					<br></br>
					<br></br>
					<span className = "rating">Rating: <b>{movie.imdbRating}</b></span>



				</div>

			</div>
			// <div className="movie-lightbox">
			// 	<img className="movie-lightbox-img" src={movie.Poster} alt=""/>
			// 	<div>
			// 		<h2>{movie.Title}</h2>
			// 		<p className="movie-lightbox-plot">{movie.Plot}</p>
			// 		<p className="movie-lightbox-director">Directed by <b>{movie.Director}</b></p>
			// 		<p className="movie-lightbox-rating">Rating: <b>{movie.imdbRating}</b></p>
			// 	</div>
			// </div>
		)

		PopupboxManager.open({content,
			config: {
				onOpen: this.lockScroll,
				onClosed: this.unlockScroll
			}
		});
    }
    dimPoster(e) {
        e.target.style.opacity = 0.5;
      }

    resetPoster(e) {
        e.target.style.opacity = 1;
    }

	render() {
		return (
			<div>
				<h1>Movies</h1>
				<div className="movies">
					{this.displayMovies()}

					<PopupboxContainer />
				</div>
			</div>

		);
	}
}

export default Movies;
